selector_to_html = {"a[href=\"#term-Rigid-registration\"]": "<dt id=\"term-Rigid-registration\">Rigid-registration</dt><dd><p>The object retains shape and size.</p></dd>", "a[href=\"#term-LBMPiezo\"]": "<dt id=\"term-LBMPiezo\">LBMPiezo</dt><dd><p>Combined LBM and piezo acquisition. Has both LBM characteristics (&gt;2 channels as beamlets) and piezo stepping. Used for pollen calibration workflows.</p></dd>", "a[href=\"#term-OME-NGFF\"]": "<dt id=\"term-OME-NGFF\">OME-NGFF</dt><dd><p>Open Microscopy Environment Next Generation File Format. A specification for storing multidimensional imaging data in Zarr format with standardized metadata for coordinate transforms and axis definitions.</p></dd>", "a[href=\"#glossary\"]": "<h1 class=\"tippy-header\" style=\"margin-top: 0;\">Glossary<a class=\"headerlink\" href=\"#glossary\" title=\"Link to this heading\">#</a></h1>", "a[href=\"#term-deconvolution\"]": "<dt id=\"term-deconvolution\">deconvolution</dt><dd><p>The process performed after segmentation to the resulting traces to infer spike times from flourescence values.</p></dd>", "a[href=\"#term-pixel-resolution\"]": "<dt id=\"term-pixel-resolution\">pixel-resolution</dt><dd><p>The length of each pixel, in micron (px/um).</p></dd>", "a[href=\"#term-source-extraction\"]": "<dt id=\"term-source-extraction\">source-extraction</dt><dd><p>Umbrella term for all of the individual processes that produce a segmented image.</p></dd>", "a[href=\"#term-volumetric-output\"]": "<dt id=\"term-volumetric-output\">volumetric output</dt><dd><p>Writing 4D data (T, Z, Y, X) to a single file with plane/frame selection support. Both TIFF (ImageJ hyperstack) and Zarr (OME-NGFF v0.5) formats are supported.</p></dd>", "a[href=\"#term-LBM\"]": "<dt id=\"term-LBM\">LBM</dt><dd><p>Light Beads Microscopy. A volumetric imaging technique where z-planes are acquired as ScanImage channels using multiple beamlets.</p></dd>", "a[href=\"#term-meanImgE\"]": "<dt id=\"term-meanImgE\">meanImgE</dt><dd><p>Enhanced mean image generated by Suite2p using filtering and sharpening operations. Useful for anatomical segmentation when available.</p></dd>", "a[href=\"#term-OutputMetadata\"]": "<dt id=\"term-OutputMetadata\">OutputMetadata</dt><dd><p>A class that computes adjusted metadata when writing subsets of data. Automatically scales z-step when selecting every Nth plane, and invalidates frame rate for non-contiguous frame selections.</p></dd>", "a[href=\"#term-segmentation\"]": "<dt id=\"term-segmentation\">segmentation</dt><dd><p>The general process of dividing an image based on the contents of that image, in our case, based on neuron location.</p></dd>", "a[href=\"#term-reactive-metadata\"]": "<dt id=\"term-reactive-metadata\">reactive metadata</dt><dd><p>Metadata that automatically adjusts based on data selection. For example, <code class=\"docutils literal notranslate\"><span class=\"pre\">dz</span></code> doubles when selecting every 2nd z-plane, and <code class=\"docutils literal notranslate\"><span class=\"pre\">fs</span></code> becomes invalid for non-contiguous frame selections.</p></dd>", "a[href=\"#term-piezo\"]": "<dt id=\"term-piezo\">piezo</dt><dd><p>Piezo z-stack acquisition. Uses a piezo actuator to step through z-positions, acquiring frames at each depth before moving.</p></dd>", "a[href=\"#term-Non-rigid-registration\"]": "<dt id=\"term-Non-rigid-registration\">Non-rigid-registration</dt><dd><p>The object is moved and transforms shape or size.</p></dd>", "a[href=\"#term-seed\"]": "<dt id=\"term-seed\">seed</dt><dd><p>A pixel or region in the image used as the <strong>starting point</strong> for growing a potential ROI.\nSeeding is based on high-intensity regions in the filtered image, and forms the initial guess\nfor the shape and location of a neuron.</p></dd>", "a[href=\"#term-mean_img\"]": "<dt id=\"term-mean_img\">mean_img</dt><dd><p>The average of the registered movie over time. Used as a baseline representation of the structural signal.</p></dd>", "a[href=\"#term-Delta-F-over-F0\"]": "<dt id=\"term-Delta-F-over-F0\">Delta F over F0</dt><dd><p><span class=\"math notranslate nohighlight\">\\(\\Delta F / F_0\\)</span> (Delta F over F naught) refers to the fractional change in fluorescence relative to a baseline signal F\u2080.\nThis is a common normalization used in calcium imaging and neural activity recordings.</p></dd>", "a[href=\"#term-anatomical-segmentation\"]": "<dt id=\"term-anatomical-segmentation\">anatomical segmentation</dt><dd><p>ROI detection based solely on static images (e.g. mean or max projection), ignoring fluorescence time dynamics.</p></dd>", "a[href=\"#term-skewness\"]": "<dt id=\"term-skewness\">skewness </dt><dd><p>How often do you get an outlier in the pixel activity across time, good for calcium indicators because theyre flat relative to a few events over the course of a recording.</p></dd>", "a[href=\"#term-functional-segmentation\"]": "<dt id=\"term-functional-segmentation\">functional segmentation</dt><dd><p>ROI detection based on pixelwise correlations across time, identifying spatially contiguous regions with similar activity patterns.</p></dd>", "a[href=\"#term-max_proj\"]": "<dt id=\"term-max_proj\">max_proj</dt><dd><p>The maximum value per pixel over time. Highlights regions with high fluorescence, useful for detecting active areas.</p></dd>"}
skip_classes = ["headerlink", "sd-stretched-link"]

window.onload = function () {
    for (const [select, tip_html] of Object.entries(selector_to_html)) {
        const links = document.querySelectorAll(` ${select}`);
        for (const link of links) {
            if (skip_classes.some(c => link.classList.contains(c))) {
                continue;
            }

            tippy(link, {
                content: tip_html,
                allowHTML: true,
                arrow: true,
                placement: 'auto-start', maxWidth: 500, interactive: false,

            });
        };
    };
    console.log("tippy tips loaded!");
};
