selector_to_html = {"a[href=\"#gui-mode\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">GUI Mode<a class=\"headerlink\" href=\"#gui-mode\" title=\"Link to this heading\">#</a></h2><p align=\"center\">\n<img alt=\"File Selection\" height=\"360\" src=\"/_images/gui/readme/01_step_file_dialog.png\"/>\n<img alt=\"Data Viewer\" height=\"360\" src=\"/_images/gui/readme/02_step_data_view.png\"/>\n</p>", "a[href=\"#download\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">Download<a class=\"headerlink\" href=\"#download\" title=\"Link to this heading\">#</a></h2><p>Download files from GitHub (auto-converts blob to raw URLs).</p>", "a[href=\"#utilities\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">Utilities<a class=\"headerlink\" href=\"#utilities\" title=\"Link to this heading\">#</a></h2>", "a[href=\"#formats\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">Formats<a class=\"headerlink\" href=\"#formats\" title=\"Link to this heading\">#</a></h2><p><strong>Input:</strong> <code class=\"docutils literal notranslate\"><span class=\"pre\">.tif</span></code>, <code class=\"docutils literal notranslate\"><span class=\"pre\">.tiff</span></code>, <code class=\"docutils literal notranslate\"><span class=\"pre\">.zarr</span></code>, <code class=\"docutils literal notranslate\"><span class=\"pre\">.bin</span></code>, <code class=\"docutils literal notranslate\"><span class=\"pre\">.h5</span></code>, <code class=\"docutils literal notranslate\"><span class=\"pre\">.hdf5</span></code>, <code class=\"docutils literal notranslate\"><span class=\"pre\">.npy</span></code>, <code class=\"docutils literal notranslate\"><span class=\"pre\">.json</span></code>\n<strong>Output:</strong> <code class=\"docutils literal notranslate\"><span class=\"pre\">.tiff</span></code>, <code class=\"docutils literal notranslate\"><span class=\"pre\">.zarr</span></code>, <code class=\"docutils literal notranslate\"><span class=\"pre\">.bin</span></code>, <code class=\"docutils literal notranslate\"><span class=\"pre\">.h5</span></code>, <code class=\"docutils literal notranslate\"><span class=\"pre\">.npy</span></code></p>", "a[href=\"#upgrade\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">Upgrade<a class=\"headerlink\" href=\"#upgrade\" title=\"Link to this heading\">#</a></h2>", "a[href=\"#convert\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">Convert<a class=\"headerlink\" href=\"#convert\" title=\"Link to this heading\">#</a></h2><p>Convert between formats with optional processing.</p>", "a[href=\"#command-line-interface\"]": "<h1 class=\"tippy-header\" style=\"margin-top: 0;\">Command Line Interface<a class=\"headerlink\" href=\"#command-line-interface\" title=\"Link to this heading\">#</a></h1><p>The <code class=\"docutils literal notranslate\"><span class=\"pre\">mbo</span></code> command provides tools for viewing, converting, and analyzing imaging data.</p>", "a[href=\"#scan-phase-analysis\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">Scan-Phase Analysis<a class=\"headerlink\" href=\"#scan-phase-analysis\" title=\"Link to this heading\">#</a></h2><p>Bidirectional resonant scanning causes alternating rows to be shifted horizontally. This tool measures that shift to help configure correction parameters.</p>", "a[href=\"#info\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">Info<a class=\"headerlink\" href=\"#info\" title=\"Link to this heading\">#</a></h2><p>Display array shape, dtype, chunk info, and metadata without loading data.</p>", "a[href=\"#notebook\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">Notebook<a class=\"headerlink\" href=\"#notebook\" title=\"Link to this heading\">#</a></h2><p>Generate template notebooks for common analysis pipelines.</p>"}
skip_classes = ["headerlink", "sd-stretched-link"]

window.onload = function () {
    for (const [select, tip_html] of Object.entries(selector_to_html)) {
        const links = document.querySelectorAll(` ${select}`);
        for (const link of links) {
            if (skip_classes.some(c => link.classList.contains(c))) {
                continue;
            }

            tippy(link, {
                content: tip_html,
                allowHTML: true,
                arrow: true,
                placement: 'auto-start', maxWidth: 500, interactive: false,

            });
        };
    };
    console.log("tippy tips loaded!");
};
